<?php

namespace Valonde\EgleBundle\Controller;

use GuzzleHttp\Client;
use Sensio\Bundle\FrameworkExtraBundle\Configuration\Method;
use Sensio\Bundle\FrameworkExtraBundle\Configuration\Route;
use Symfony\Bundle\FrameworkBundle\Controller\Controller;
use Symfony\Component\HttpFoundation\File\UploadedFile;
use Symfony\Component\HttpFoundation\Request;
use GuzzleHttp\Psr7\Request as gRequest;

class DefaultController extends Controller
{

    /**
     * @Assert\File(maxSize="6000000")
     */
    private $file;

    /**
     * @Route("/", name="valonde_egle_homepage")
     * @Method("GET")
     */
    public function indexAction()
    {
        return $this->render('ValondeEgleBundle:Default:index.html.twig');
    }

    /**
     * @Route("/post-to-imgur", name="valonde_egle_post_imgur")
     * @Method("POST")
     */
    public function postToImgurAction(Request $request)
    {
        //Init guzzle post request https://api.imgur.com/3/image.json
        $url = "https://api.imgur.com/3/image.json";
        $client_id = "a719aa61dc69b5b";
        $post_data = array(
          'image' => "data:image/png;base64,"
        );
        $client = new Client([
            // Base URI is used with relative requests
            'base_uri' => $url,
            // You can set any number of default request options.
            'timeout'  => 15.0,
        ]);
        dump($request->files);

        $gRequest  = new gRequest('POST', 'https://api.imgur.com/3/image.json', [
                        'headers' => [
                            'Authorization: Client-ID' => $client_id
                        ],
                        'body' => $post_data,
                        'image' => "data:image/png;base64,"

        ]);

        $gResponse = $client->send($gRequest, ['timeout' => 2]);

        dump($gResponse);

        return $this->render('ValondeEgleBundle:Default:index.html.twig', array('gResponse' => $gRequest));
    }
}
